impl OutputProcessor {
    fn format_number(&self, value: f64) -> String {
        if self.config.scientific_notation {
            format!("{:.prec$e}", value, prec = self.config.precision)
        } else {
            format!("{:.prec$}", value, prec = self.config.precision)
        }
    }
