pub struct MnaSystem {
    /// ç³»ç»ŸçŸ©é˜µ A (ç³»æ•°çŸ©é˜µ)
    pub matrix: DMatrix,
    /// å³ä¾§å‘é‡ z
    pub rhs: DVector,
    /// æœªçŸ¥å˜é‡å‘é‡ x (èŠ‚ç‚¹ç”µå‹ + ç”µå‹æºç”µæµ)
    pub unknowns: DVector,
    /// èŠ‚ç‚¹IDåˆ°çŸ©é˜µè¡Œåˆ—ç´¢å¼•çš„æ˜ å°„
    pub node_map: HashMap,
    /// ç”µå‹æºåç§°åˆ°ç”µæµå˜é‡ç´¢å¼•çš„æ˜ å°„
    pub voltage_source_map: HashMap,
    /// ç³»ç»Ÿæ€»å¤§å°
    pub size: usize,
    /// èŠ‚ç‚¹æ•°é‡ï¼ˆä¸åŒ…æ‹¬æ¥åœ°èŠ‚ç‚¹ï¼‰
    pub num_nodes: usize,
    /// ç”µå‹æºæ•°é‡
