        let residual_norm = residual.norm();

        let solve_time = start_time.elapsed().as_secs_f64();

        let stats = SolverStats {
            method_used: SolverMethod::Lu,
            iterations: 1,
            residual_norm,
            solve_time,
            success: residual_norm , rhs: &[f64]) -> Result, SolverStats)> {
