å…±è½­æ¢¯åº¦æ³•é€‚ç”¨äºå¯¹ç§°æ­£å®šçŸ©é˜µï¼Œå…·æœ‰æœ€ä¼˜çš„æ”¶æ•›æ€§è´¨ã€‚
impl LinearSolver {
    fn solve_cg_sparse(&self, matrix: &CsMat, rhs: &[f64]) -> Result, SolverStats)> {
        let start_time = Instant::now();
        let n = matrix.rows();

        // åˆå§‹åŒ–è§£å‘é‡
        let mut x = vec![0.0; n];
        let mut r = rhs.to_vec(); // åˆå§‹æ®‹å·® r = b - Ax
        let mut p = r.clone(); // æœç´¢æ–¹å‘

        let mut r_dot_r = vector_dot(&r, &r);
        let initial_residual_norm = r_dot_r.sqrt();

        let mut iteration = 0;
        let mut residual_norm = initial_residual_norm;

        while iteration  self.config.tolerance {
            // è®¡ç®— Ap
            let ap = sparse_matrix_vector_multiply(matrix, &p);

            // è®¡ç®—æ­¥é•¿ Î± = (rÂ·r) / (pÂ·Ap)
            let p_dot_ap = vector_dot(&p, &ap);
            let alpha = r_dot_r / p_dot_ap;

            // æ›´æ–°è§£å‘é‡ x = x + Î±p
            for i in 0..n {
                x[i] += alpha * p[i];
            }

            // æ›´æ–°æ®‹å·® r = r - Î±Ap
            for i in 0..n {
                r[i] -= alpha * ap[i];
            }

            // è®¡ç®—æ–°çš„æ®‹å·®å†…ç§¯
            let r_dot_r_new = vector_dot(&r, &r);
            residual_norm = r_dot_r_new.sqrt();

            // è®¡ç®—æœç´¢æ–¹å‘æ›´æ–°ç³»æ•° Î² = (r_newÂ·r_new) / (r_oldÂ·r_old)
            let beta = r_dot_r_new / r_dot_r;

            // æ›´æ–°æœç´¢æ–¹å‘ p = r + Î²p
            for i in 0..n {
                p[i] = r[i] + beta * p[i];
            }

            r_dot_r = r_dot_r_new;
            iteration += 1;
        }

        let solve_time = start_time.elapsed().as_secs_f64();

        let stats = SolverStats {
            method_used: SolverMethod::Cg,
            iterations: iteration,
            residual_norm,
            solve_time,
            success: residual_norm 
BiCGSTABæ–¹æ³•
BiCGSTABæ–¹æ³•é€‚ç”¨äºéå¯¹ç§°çŸ©é˜µï¼Œæ˜¯BiCGæ–¹æ³•çš„æ”¹è¿›ç‰ˆæœ¬ã€‚
impl LinearSolver {
    fn solve_bicgstab_sparse(&self, matrix: &CsMat, rhs: &[f64]) -> Result, SolverStats)> {
        let start_time = Instant::now();
        let n = matrix.rows();

        // åˆå§‹åŒ–è§£å‘é‡
        let mut x = vec![0.0; n];
        let mut r = rhs.to_vec(); // åˆå§‹æ®‹å·®
        let mut r_hat = r.clone(); // ä¼ªæ®‹å·®

        let mut rho = 1.0;
        let mut alpha = 1.0;
        let mut omega = 1.0;

        let mut p = vec![0.0; n];
        let mut v = vec![0.0; n];

        let initial_residual_norm = vector_norm(&r);
        let mut residual_norm = initial_residual_norm;
        let mut iteration = 0;

        while iteration  self.config.tolerance {
            let rho_old = rho;
            rho = vector_dot(&r_hat, &r);

            let beta = (rho / rho_old) * (alpha / omega);

            // æ›´æ–° p
            for i in 0..n {
                p[i] = r[i] + beta * (p[i] - omega * v[i]);
            }

            // è®¡ç®— v = Ap
            v = sparse_matrix_vector_multiply(matrix, &p);

            alpha = rho / vector_dot(&r_hat, &v);

            // è®¡ç®— s = r - Î±v
            let mut s = vec![0.0; n];
            for i in 0..n {
                s[i] = r[i] - alpha * v[i];
            }

            // è®¡ç®— t = As
            let t = sparse_matrix_vector_multiply(matrix, &s);

            omega = vector_dot(&t, &s) / vector_dot(&t, &t);

            // æ›´æ–°è§£å‘é‡å’Œæ®‹å·®
            for i in 0..n {
                x[i] += alpha * p[i] + omega * s[i];
                r[i] = s[i] - omega * t[i];
            }

            residual_norm = vector_norm(&r);
            iteration += 1;
        }

        let solve_time = start_time.elapsed().as_secs_f64();

        let stats = SolverStats {
            method_used: SolverMethod::BiCgStab,
            iterations: iteration,
            residual_norm,
            solve_time,
            success: residual_norm 
