pub fn is_symmetric(matrix: &CsMat, tolerance: f64) -> bool {
    for (i, j, &value) in matrix.iter() {
        if let Some(&other_value) = matrix.get(j, i) {
            if (value - other_value).abs() > tolerance {
                return false;
            }
        } else {
            return false;
        }
    }
    true
}

pub fn auto_select_solver(matrix: &CsMat) -> SolverMethod {
    // æ£€æŸ¥çŸ©é˜µæ˜¯å¦å¯¹ç§°
    if is_symmetric(matrix, 1e-12) {
        // å¯¹äºå¯¹ç§°çŸ©é˜µï¼Œä¼˜å…ˆä½¿ç”¨CGæ–¹æ³•
        SolverMethod::Cg
    } else {
        // å¯¹äºéå¯¹ç§°çŸ©é˜µï¼Œä½¿ç”¨BiCGSTABæ–¹æ³•
        SolverMethod::BiCgStab
    }
}

