    pub fn calculate_statistics(&self, results: &SimulationResult) -> Result {
        let mut node_voltage_stats = HashMap::new();
        let mut current_stats = HashMap::new();

        // è®¡ç®—èŠ‚ç‚¹ç”µå‹ç»Ÿè®¡
        for (node_name, voltages) in &results.node_voltages {
